import React, { useState, useEffect } from "react";
import {useHistory, useParams} from 'react-router-dom'
import Nav from "../nav/nav"
import '../../style.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import AddResumeData from "./AddResumeData";
import EditResume from './EditResume'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';


const ViewResume = () => {

    const [resumeData, setResumeData] = useState([]);


    const data = {
      data: resumeData
  }

  const [modal, setModal] = useState(false);

  const toggle = () => setModal(!modal);


    // const history = useHistory();

    // const resumeInfo = {
    //     patientID:id, 
    //     careproviderID: careprovider
    // }

    // const resumeInfoEdit = {
    //     patientID:id, 
    //     patientEditID: patientEdit
    // }


  useEffect(() => {

    async function fetchData() {
      const res = await fetch(`http://localhost:4000/resume`);
      res.json().then((res) => setResumeData(res));

    }
    fetchData();
  });

//   // Go Back function
//   const goBack = () => {
//     history.goBack()
// }

  return (
    <div>
    {/* <Container> */}
      <Nav />
        <h2 className="work-h2">My Work Experience</h2>
        <AddResumeData data={resumeData} />
        {/* <Row>
          <Col> */}
            <section className="resume">
            {resumeData.map((data) =>
                <div className="work-div">
                    <h3>{data.job}</h3>
                    <h4>{data.jobTitle}</h4>
                    <h5>{data.date}</h5>
        <Button color="danger" className="edit-btn" onClick={toggle}>Edit Resume</Button>
        <Button color="danger" className="edit-btn" onClick={toggle}>Delete Resume</Button>
                    <ul>
                        <li>{data.note1}</li>
                        <li>{data.note2}</li>
                        <li>{data.note3}</li>
                        <li>{data.note4}</li>
                    </ul>
                    <hr className="work-seperation" />
                </div>
                )}
                </section>
            {/* </Col>
          </Row>
    </Container> */}
    </div>
  );
};

export default ViewResume;
